import React, { useState } from 'react';
import Accordion from './components/Accordion';
import Search from './components/Search';
import Dropdown from './components/Dropdown';
import Translate from './components/Translate';
import Route from './components/Route';

const options = [
    { 
        label: 'Color Red',
        value: 'red'
    },
    {
        label: 'Color Blue',
        value: 'blue'
    },
    {
        label: 'Color Green',
        value: 'green'
    }
];


const items =[
    {
        title:'Title Area One',
        content: 'Yes, there is content here.'
    },
    {
        title:'Title Area Two',
        content: 'Content displays here.'
    },
    {
        title:'Title Area Three',
        content: 'Body copy here please.'
    },
];

const showAccordion = () => {
    if (window.location.pathname === '/')
    return (
        <div> <Accordion items={items} /></div>
    );
};

const showWiki = () => {
    if (window.location.pathname === '/wiki')
        return (
            <div> <Search /></div>
        );
};

const showDropdown = () => {
    if (window.location.pathname === '/dropdown')
        return (
            <div> <Dropdown /></div>
        );
};

const showTranslate = () => {
    if (window.location.pathname === '/translate')
        return (
            <div><Translate /></div>
        );
};

export default () => {

    return (
        <div>
            {showAccordion()}
            {showWiki()}
            {showDropdown()}
            {showTranslate()}
        </div>
    );
};
